import React, { useState, useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";
import NavDropdown from "react-bootstrap/NavDropdown";
import { Navbar, Nav } from "react-bootstrap";
import Container from "react-bootstrap/Container";
function UserProfile() {
  //   function logOut() {
  //     localStorage.clear();
  //     navigate('/');
  // }

  const [userInfo, setUserInfo] = useState({});
  useEffect(() => {
    const storedUserInfo = localStorage.getItem("user-info");

    if (storedUserInfo) {
      const parsedUserInfo = JSON.parse(storedUserInfo);
      setUserInfo(parsedUserInfo[0]);
    }
  }, []);
  const handleSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <Navbar expand="lg" className="navbar navbar-dark bg-dark">
        <Container>
          <Navbar.Brand href="#home">Navigation</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto"></Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <h3 className="d-flex justify-content-center align-items-center">
        User Profile
      </h3>
      {userInfo ? (
        <div
          className="username d-flex justify-content-center align-items-center 50-w vh-100 bg-white"
          action=""
          onSubmit={handleSubmit}
        >
          <table className=" justify-content-center ">
            <div className="md-3 d-flex">
              <div htmlFor="f_name"> First Name : </div>
              <p className="d-flex mr-auto">{userInfo.first_name}</p>
            </div>
            <div className="md-1 d-flex">
              <div htmlFor="l_name"> Last Name : </div>
              <p className="d-flex mr-auto"> {userInfo.last_name}</p>
            </div>

            <div className="md-1 d-flex">
              <div htmlFor="address"> Address : </div>
              <p className="d-flex mr-auto">{userInfo.address}</p>
            </div>
            <div className="md-1 d-flex">
              <div htmlFor="phone"> Phone : </div>
              <p className="d-flex mx-4">{userInfo.phone_Number}</p>
            </div>
            <div className="md-1 d-flex">
              <div htmlFor="email"> Email : </div>
              <p className="d-flex mx-4">{userInfo.email}</p>
            </div>

            <div className="md-1 d-flex">
              <div htmlFor="birthday"> Birthdate : </div>
              <p className="d-flex ">{userInfo.birthdate}</p>
            </div>
          </table>

          {/* Display other user-specific data */}
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
}

export default UserProfile;
